import React, { useEffect, useState } from 'react';
import Typewriter from "typewriter-effect";

const descriptors = [
    "Consistent",
    "Dedicated to Continuous Learning",
    "Innovative",
    "Problem Solver",
    "Team Player",
    "Analytical Thinker",
    "Passionate about Technology",
    "Adaptable",
    "Collaborative",
    "Detail-Oriented"
  ];

