//       </footer>
//     );
//   }
  
const cursorStyle = {
    display: 'inline-block',
    animation: 'swirly-cursor 2s linear infinite'
  };
  
  const keyframes = `
  @keyframes swirly-cursor {
    0% {
      transform: translate(0, 0) rotate(0);
    }
    25% {
      transform: translate(5px, 10px) rotate(90deg);
    }
    50% {
      transform: translate(10px, 0) rotate(180deg);
    }
    75% {
      transform: translate(5px, -10px) rotate(270deg);
    }
    100% {
      transform: translate(0, 0) rotate(360deg);
    }
  }`;
  
  function Footer() {
  
    return (
      <footer className="footer">
        <div className="footer-content">
          <div className="descriptor-typing">
            <Typewriter
              options={{
                strings: descriptors,
                autoStart: true,
                loop: true,
                delay: 100,
                deleteSpeed: 200,
                cursor: " " // Set a space to hide the default cursor
              }}
            />
            <div style={cursorStyle} className="typewriter-cursor">|</div> {/* Custom cursor */}
          </div>
        </div>
        <style>
          {keyframes}
        </style>
      </footer>
    );
  }
  

export default Footer;
