import React, { useState, useEffect } from 'react';
import './styles.css'; // Make sure to import the CSS for swirly animation

const SwirlyTypewriter = ({ text, typingSpeed = 100, pauseTime = 2000 }) => {
  const [displayText, setDisplayText] = useState('');
  const [currentCharIndex, setCurrentCharIndex] = useState(0);

  useEffect(() => {
    const typeCharacter = () => {
      if (currentCharIndex < text.length) {
        setDisplayText((prev) => prev + text[currentCharIndex]);
        setCurrentCharIndex((prev) => prev + 1);
      } else {
        setTimeout(() => {
          setDisplayText('');
          setCurrentCharIndex(0);
        }, pauseTime);
      }
    };

    const typingInterval = setInterval(typeCharacter, typingSpeed);

    return () => clearInterval(typingInterval);
  }, [currentCharIndex, text, typingSpeed, pauseTime]);

  return (
    <div className="swirly-container">
      {displayText.split('').map((char, index) => (
        <span key={index} className="swirly-char">
          {char}
        </span>
      ))}
    </div>
  );
};

function Footer() {
  const descriptors = ["Descriptor 1", "Descriptor 2", "Descriptor 3"]; // Add your descriptors here
  const [currentDescriptor, setCurrentDescriptor] = useState(descriptors[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDescriptor((prev) => {
        const currentIndex = descriptors.indexOf(prev);
        const nextIndex = (currentIndex + 1) % descriptors.length;
        return descriptors[nextIndex];
      });
    }, 5000); // Change descriptor every 5 seconds

    return () => clearInterval(interval);
  }, [descriptors]);

  return (
    <footer className="footer">
      <div className="footer-content">
        <div className="descriptor-typing">
          <SwirlyTypewriter text={currentDescriptor} />
        </div>
      </div>
    </footer>
  );
}

export default Footer;
