import React, { useEffect, useState } from 'react';
import Typewriter from "typewriter-effect";

const descriptors = [
    "Consistent",
    "Dedicated to Continuous Learning",
    "Innovative",
    "Problem Solver",
    "Team Player",
    "Analytical Thinker",
    "Passionate about Technology",
    "Adaptable",
    "Collaborative",
    "Detail-Oriented"
  ];

// function Footer() {
//   return (
//     <footer className="footer">
//       <p>&copy; {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
//     </footer>
//   );
// }
function Footer() {
    const [currentText, setCurrentText] = useState("My Name");
  
    useEffect(() => {
      // Change text after a delay
      const timer = setTimeout(() => {
        setCurrentText("See also:");
      }, 3000); // Show "See also:" after 3 seconds
  
      return () => clearTimeout(timer);
    }, []);
  
    return (
      <footer className="footer">
        <div className="footer-content">
          <div className="text-animation">
            {currentText} {/* Display the current text */}
          </div>
          <div className="descriptor-flow">
            {descriptors.join(', ')} {/* Join descriptors with commas */}
          </div>
        </div>
      </footer>
    );
  }
  
  export default Footer;
