import React, { useEffect, useState } from 'react';
import Typewriter from "typewriter-effect";

const descriptors = [
    "Consistent",
    "Dedicated to Continuous Learning",
    "Innovative",
    "Problem Solver",
    "Team Player",
    "Analytical Thinker",
    "Passionate about Technology",
    "Adaptable",
    "Collaborative",
    "Detail-Oriented"
  ];

// function Footer() {
//   return (
//     <footer className="footer">
//       <p>&copy; {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
//     </footer>
//   );
// }
function Footer() {
    const [text, setText] = useState('My Name');
    const [descriptorIndex, setDescriptorIndex] = useState(0);
    const [fullText, setFullText] = useState('');
  
    useEffect(() => {
      let typingInterval;
      let delayTimeout;
  
      const updateText = () => {
        const currentDescriptor = descriptors[descriptorIndex];
        let newText = text;
        if (newText.length < currentDescriptor.length) {
          newText += currentDescriptor.charAt(newText.length);
          setText(newText);
        } else {
          clearInterval(typingInterval);
          delayTimeout = setTimeout(() => {
            setDescriptorIndex((prevIndex) => (prevIndex + 1) % descriptors.length);
            setText(text + ', ');
          }, 1000); // Pause after completing the current descriptor
        }
      };
  
      typingInterval = setInterval(updateText, 100); // Speed of typing effect
  
      return () => {
        clearInterval(typingInterval);
        clearTimeout(delayTimeout);
      };
    }, [descriptorIndex, text]);
  
    useEffect(() => {
      // Start with the initial text and then append descriptors
      setFullText(descriptors.join(', '));
    }, []);
  
    return (
      <footer className="footer">
        <div className="footer-content">
          <div className="text-animation">
            {text}
          </div>
          <div className="descriptor-flow">
            {/* Flow the fullText across the page */}
            {fullText}
          </div>
        </div>
      </footer>
    );
  }
  
  export default Footer;
