import React, { useState, useEffect } from 'react';
import Typewriter from 'typewriter-effect';

const SwirlyTypewriter = ({ text }) => {
  const [currentText, setCurrentText] = useState('');
  
  useEffect(() => {
    const typewriter = new Typewriter(null, {
      loop: true,
      delay: 100,
      deleteSpeed: 200,
      cursor: "",
    });

    typewriter
      .typeString(text)
      .pauseFor(2000)
      .deleteAll()
      .start()
      .callFunction((state) => {
        setCurrentText(state.elements.wrapper.innerHTML);
      });

    typewriter.options.onCreateTextNode = (character) => {
      const span = document.createElement('span');
      span.innerHTML = character;
      span.classList.add('swirly-char');
      return span;
    };

    return () => {
      typewriter.stop();
    };
  }, [text]);

  return <div className="swirly-container" dangerouslySetInnerHTML={{ __html: currentText }} />;
};

function Footer() {
  const descriptors = ["Descriptor 1", "Descriptor 2", "Descriptor 3"]; // Add your descriptors here
  const [currentDescriptor, setCurrentDescriptor] = useState(descriptors[0]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDescriptor((prev) => {
        const currentIndex = descriptors.indexOf(prev);
        const nextIndex = (currentIndex + 1) % descriptors.length;
        return descriptors[nextIndex];
      });
    }, 5000); // Change descriptor every 5 seconds

    return () => clearInterval(interval);
  }, [descriptors]);

  return (
    <footer className="footer">
      <div className="footer-content">
        <div className="descriptor-typing">
          <SwirlyTypewriter text={currentDescriptor} />
        </div>
      </div>
    </footer>
  );
}

export default Footer;
