import React from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Chip from '@mui/material/Chip';
import Stack from '@mui/material/Stack';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const slides = [
  { step: "Step 1: Research Other Competitors", image: "image_url_1.jpg" },
  { step: "Step 2: Prototype", image: "image_url_2.jpg" },
  // Add more steps as needed
];

const Provenance = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    // nextArrow: <SampleNextArrow />,
    // prevArrow: <SamplePrevArrow />
  };

  return (
    <div style={{ paddingTop: '100px' }}>
      <h1 style={{ fontSize: '3rem' }} className="text-center mb-20">Provenance Security</h1>
      <p style={{ fontSize: '2rem', paddingLeft: '50px' }}>Overview</p>
      <p style={{ fontSize: '1.2rem', paddingLeft: '50px', paddingTop: '20px' }}>
        Provenance Security is an innovative cybersecurity platform aiming to help security analysts identify and resolve alerts 
        through the use of provenance graphs. These graphs along with additional statistics provide data to better understand potential threats. 
        Our goal with this website is to provide an interactive experience for investigation and resolution of any security issues. 
        <br></br>
        <br></br>
        My team and I at Illinois Design Consulting meticulously designed the user interface of this cybersecurity website to provide security analysts 
        an intuitive and visually appealing experience. We wanted the navigation experience to be effortless.
      </p>
      <br></br>
      <p style={{ fontSize: '2rem', paddingLeft: '50px' }}>Skills Used</p>
      <Stack direction="row" spacing={1} sx={{ mt: 2, paddingLeft: '50px' }}>
        <Chip label="Figma" sx={{ fontSize: '1.2rem', padding: '8px 16px' }} />
        <Chip label="UI/UX" sx={{ fontSize: '1.2rem', padding: '8px 16px' }} />
        <Chip label="Miro" sx={{ fontSize: '1.2rem', padding: '8px 16px' }} />
        <Chip label="Visual Design" sx={{ fontSize: '1.2rem', padding: '8px 16px' }} />
        <Chip label="Prototyping" sx={{ fontSize: '1.2rem', padding: '8px 16px' }} />
      </Stack>

      {/* Slider */}
      
      <div id="provenance-container" style={{ backgroundColor: '#fff', width: '100%', height: '300px' }}>
      <Slider {...settings}>
        {slides.map((project, index) => (
          <div key={index} className="project-slide">
            <div className="grid grid-cols-2 gap-4">
              <div className="project-image">
                <img src={project.image} alt={project.image} className="custom-image-size" />
              </div>
              <div className="project-text">
                <h2>{project.step}</h2>
              </div>
            </div>
          </div>
        ))}
      </Slider>
      </div>
    </div>
  );
}

