import React from 'react';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

const Provenance = () => {
  const slides = [
    { step: "Step 1: Research Other Competitors", image: "image_url_1.jpg" },
    { step: "Step 2: Prototype", image: "image_url_2.jpg" },
    // Add more steps as needed
  ];

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: true,
    prevArrow: <CustomPrevArrow />,
    nextArrow: <CustomNextArrow />,
  };

  const CustomPrevArrow = (props) => (
    <div {...props} style={{ ...props.style, display: 'block', background: 'red' }}>Previous</div>
  );

  const CustomNextArrow = (props) => (
    <div {...props} style={{ ...props.style, display: 'block', background: 'green' }}>Next</div>
  );

  return (
    <div style={{ paddingTop: '100px' }}>
      {/* Your other content */}
      
      {/* Slider */}
      <div id="provenance-container" style={{ backgroundColor: '#fff', width: '100%', height: '600px' }}>
        <Slider {...settings}>
          {slides.map((slide, index) => (
            <div key={index}>
              <img src={slide.image} alt={`Step ${index + 1}`} style={{ maxWidth: '100%', maxHeight: '100%' }} />
              <p>{slide.step}</p>
            </div>
          ))}
        </Slider>
      </div>
    </div>
  );
}

export default Provenance;
