import React, { useEffect, useState } from 'react';
import Typewriter from "typewriter-effect";

const descriptors = [
    "Consistent",
    "Dedicated to Continuous Learning",
    "Innovative",
    "Problem Solver",
    "Team Player",
    "Analytical Thinker",
    "Passionate about Technology",
    "Adaptable",
    "Collaborative",
    "Detail-Oriented"
  ];

// function Footer() {
//   return (
//     <footer className="footer">
//       <p>&copy; {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
//     </footer>
//   );
// }
function Footer() {
    const [text, setText] = useState('');
    const [index, setIndex] = useState(0);
    const [isTyping, setIsTyping] = useState(true);
  
    useEffect(() => {
      if (isTyping) {
        if (index < descriptors.length) {
          const currentDescriptor = descriptors[index];
          setText(prev => prev + currentDescriptor.charAt(prev.length));
  
          if (prev.length === currentDescriptor.length - 1) {
            setIsTyping(false);
            setTimeout(() => {
              setText('');
              setIndex(index + 1);
              setIsTyping(true);
            }, 1000); // Pause after completing the current descriptor
          }
        } else {
          // Reset index to loop
          setIndex(0);
        }
      }
    }, [text, index, isTyping]);
  
    return (
      <footer className="footer">
        <div className="footer-content">
          <div className="text-animation">
            {text || "My Name"}
          </div>
          <div className="descriptor-flow">
            {/* This content will move across the page */}
            {descriptors.join(', ')}
          </div>
        </div>
      </footer>
    );
  }
  
  export default Footer;
