import React, { useEffect, useState } from 'react';
import Typewriter from "typewriter-effect";

const descriptors = [
    "Consistent",
    "Dedicated to Continuous Learning",
    "Innovative",
    "Problem Solver",
    "Team Player",
    "Analytical Thinker",
    "Passionate about Technology",
    "Adaptable",
    "Collaborative",
    "Detail-Oriented"
  ];

// function Footer() {
//   return (
//     <footer className="footer">
//       <p>&copy; {new Date().getFullYear()} Your Company Name. All rights reserved.</p>
//     </footer>
//   );
// }
function Footer() {
    const [text, setText] = useState('');
    const [index, setIndex] = useState(0);
    const [isTyping, setIsTyping] = useState(true);
  
    useEffect(() => {
      let typingInterval;
      if (isTyping) {
        typingInterval = setInterval(() => {
          setText((prev) => {
            const currentDescriptor = descriptors[index];
            if (prev.length < currentDescriptor.length) {
              return prev + currentDescriptor.charAt(prev.length);
            } else {
              setIsTyping(false);
              return prev;
            }
          });
        }, 100); // Speed of typing effect
      } else {
        setTimeout(() => {
          setText('');
          setIndex((prevIndex) => (prevIndex + 1) % descriptors.length);
          setIsTyping(true);
        }, 1000); // Pause after completing the current descriptor
      }
  
      return () => clearInterval(typingInterval);
    }, [isTyping, index]);
  
    return (
      <footer className="footer">
        <div className="footer-content">
          <div className="text-animation">
            {text || "My Name"}
          </div>
          <div className="descriptor-flow">
            {/* This content will move across the page */}
            {descriptors.join(', ')}
          </div>
        </div>
      </footer>
    );
  }
  
  export default Footer;
